/* 2048 Game CSS */

* {
  box-sizing: border-box;
}

body {
  background-color: #faf8ef;
  font-family: "Clear Sans", Helvetica, Arial, sans-serif;
  margin: 0 auto;
  padding: 20px;
  max-width: 500px;
}

#app {
  max-width: 500px;
  margin: 0 auto;
}

/* Header */
#header-top {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

h1 {
  font-size: 80px;
  font-weight: 700;
  color: #776e65;
  margin: 0;
  line-height: 1;
}

#scores {
  display: flex;
  gap: 8px;
}

.score-box {
  background-color: #bbada0;
  border-radius: 3px;
  padding: 8px 16px;
  text-align: center;
  min-width: 64px;
}

.score-box label {
  display: block;
  font-size: 13px;
  font-weight: 700;
  color: #eee4da;
  text-transform: uppercase;
}

.score-box span {
  display: block;
  font-size: 25px;
  font-weight: 700;
  color: white;
}

#controls {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 12px;
}

.tagline {
  color: #776e65;
  font-size: 18px;
  margin: 0;
}

#btn-new {
  background-color: #8f7a66;
  color: #f9f6f2;
  font-size: 18px;
  font-weight: 700;
  border: none;
  border-radius: 3px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}

#btn-new:hover {
  background-color: #9f8b77;
}

/* Board */
#board-wrapper {
  position: relative;
  margin-top: 20px;
}

#board {
  position: relative;
  background-color: #bbada0;
  border-radius: 6px;
  padding: 15px;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 15px;
  width: 100%;
  box-sizing: border-box;
  aspect-ratio: 1 / 1;
}

.cell {
  background-color: rgba(238, 228, 218, 0.35);
  border-radius: 3px;
}

/* Tiles */
.tile {
  position: relative;
  border-radius: 3px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 35px;
  transition: none;
  user-select: none;
  z-index: 10;
}

/* Tile Colors by Value */
.tile[data-value="2"] {
  background-color: #eee4da;
  color: #776e65;
}

.tile[data-value="4"] {
  background-color: #ede0c8;
  color: #776e65;
}

.tile[data-value="8"] {
  background-color: #f2b179;
  color: #f9f6f2;
}

.tile[data-value="16"] {
  background-color: #f59563;
  color: #f9f6f2;
}

.tile[data-value="32"] {
  background-color: #f67c5f;
  color: #f9f6f2;
}

.tile[data-value="64"] {
  background-color: #f65e3b;
  color: #f9f6f2;
}

.tile[data-value="128"] {
  background-color: #edcf72;
  color: #f9f6f2;
  font-size: 35px;
  box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.3);
}

.tile[data-value="256"] {
  background-color: #edcc61;
  color: #f9f6f2;
  font-size: 35px;
  box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.3);
}

.tile[data-value="512"] {
  background-color: #edc850;
  color: #f9f6f2;
  font-size: 35px;
  box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.3);
}

.tile[data-value="1024"] {
  background-color: #edc53f;
  color: #f9f6f2;
  font-size: 28px;
  box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.4);
}

.tile[data-value="2048"] {
  background-color: #edc22e;
  color: #f9f6f2;
  font-size: 28px;
  box-shadow: 0 0 30px 10px rgba(243, 215, 116, 0.5);
}

.tile.tile-super {
  background-color: #3c3a32;
  color: #f9f6f2;
  font-size: 24px;
}

/* Tile Font Size by Value */
.tile[data-value="128"],
.tile[data-value="256"],
.tile[data-value="512"] {
  font-size: 35px;
}

.tile[data-value="1024"],
.tile[data-value="2048"] {
  font-size: 28px;
}

/* Animations */
@keyframes tile-appear {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}

@keyframes tile-pop {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.2);
  }
  100% {
    transform: scale(1);
  }
}

@keyframes score-bump {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
  }
  100% {
    transform: scale(1);
  }
}

.tile-new {
  animation: tile-appear 100ms ease forwards;
}

.tile-merge {
  animation: tile-pop 150ms ease forwards;
}

.score-bump {
  animation: score-bump 200ms ease;
}

/* Overlay */
#overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(238, 228, 218, 0.73);
  border-radius: 6px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  z-index: 100;
  gap: 20px;
}

#overlay-msg {
  font-size: 60px;
  font-weight: 700;
  color: #776e65;
  margin: 0;
}

#btn-try-again {
  background-color: #8f7a66;
  color: #f9f6f2;
  font-size: 18px;
  font-weight: 700;
  border: none;
  border-radius: 3px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}

#btn-try-again:hover {
  background-color: #9f8b77;
}

.hidden {
  display: none !important;
}

/* Instructions */
#instructions {
  color: #776e65;
  font-size: 14px;
  margin-top: 20px;
  text-align: center;
}

/* Responsive Design */
@media (max-width: 520px) {
  h1 {
    font-size: 50px;
  }

  .tile {
    font-size: 30px;
  }

  .tile[data-value="128"],
  .tile[data-value="256"],
  .tile[data-value="512"] {
    font-size: 26px;
  }

  .tile[data-value="1024"],
  .tile[data-value="2048"] {
    font-size: 22px;
  }

  .tile.tile-super {
    font-size: 18px;
  }

  #overlay-msg {
    font-size: 40px;
  }
}

